function grating = makeCosineGrating(stimSize,orient,lambda , con , phase );if nargin == 3    con = 1.0;    phase = 0;elseif nargin == 4    phase = randi(180); %jitter the phase to avoid after imagesend[ x y ] = meshgrid(-stimSize/2+1:stimSize/2);% grating = con * cos((x.*sin(orient)+y.*cos(orient))*2*pi/lambda+phase) * 0.5 + 0.5;%%% lambda=50;grating = con * cos((x.*sin(orient)+y.*cos(orient))*2*pi/lambda+phase) * 0.5 +.05;%   imshow(grating)%  shg